// frontend/src/components/MediaList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css'
import { useNavigate } from 'react-router-dom';
const defaultList = [
    {
        "title": "Yamuna",
        "description": "HELLO",
        "thumbnailUrl": "https://res.cloudinary.com/djxfeyput/image/upload/v1722583764/ov4omdwsrvvfj0e6wjyb.png",
        "videoUrl": "https://res.cloudinary.com/djxfeyput/video/upload/v1722583769/ukz8tarobj0b9u03a3af.mp4"
    },
    {
        "title": "MERN APP",
        "description": "HELLO",
        "thumbnailUrl": "https://res.cloudinary.com/djxfeyput/image/upload/v1722583764/ov4omdwsrvvfj0e6wjyb.png",
        "videoUrl": "https://res.cloudinary.com/djxfeyput/video/upload/v1722583769/ukz8tarobj0b9u03a3af.mp4"
    },
    {
        "title": "Video upload app",
        "description": "HELLO",
        "thumbnailUrl": "https://res.cloudinary.com/djxfeyput/image/upload/v1722583764/ov4omdwsrvvfj0e6wjyb.png",
        "videoUrl": "https://res.cloudinary.com/djxfeyput/video/upload/v1722583769/ukz8tarobj0b9u03a3af.mp4"
    }
]
const MediaList = () => {
    const navigation = useNavigate()
    const [mediaList, setMediaList] = useState(defaultList);
    useEffect(() => {
        const fetchMedia = async () => {
            try {
                const response = await axios.get('http://localhost:5000/media');
                setMediaList(response.data);
            } catch (error) {
                console.error('Error fetching media list:', error);
            }
        };

        fetchMedia();
    }, []);



    const getDetails = (id) => {
        navigation(`/media/${id}`)
    }
    return (
        <div className='container'>
            <h1 className='heading'>Media list</h1>
            <button
                onClick={() => navigation('/upload')}
                style={{
                    backgroundColor: "#0062ff",
                    color: 'white',
                    alignItems: 'center',
                    textAlign: 'center',
                    justifyContent: "center",
                    alignSelf: 'flex-end',
                    borderWidth: 0,
                    fontSize: 12,
                    fontWeight: 600,
                    cursor: 'pointer',
                    marginTop: 0,
                    padding: 10,
                    borderRadius: 10,
                    width: "10%",
                    marginBottom: 5
                }}
            > Upload Image</button>
            <div className='innercontainer2' >
                {mediaList.map(media => (
                    <div className='container3' key={media.id} >

                        { mediaList !== []?
                            <div style={{ backgroundColor: 'pink' }}
                                onClick={() => getDetails(media.id)}
                            >
                                <img src={media.thumbnailUrl} alt={media.title} height='130' />
                                <h2>{media.title}</h2>
                            </div>
                            :
                            <h2>Upload Data </h2>
                        }

                    </div>
                ))}
            </div>
        </div>
    );
};

export default MediaList;
