// frontend/src/components/MediaList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigation } from 'react-router-dom';
import './index.css'
const MediaList = () => {
    const [mediaList, setMediaList] = useState([]);
    useEffect(() => {
        const fetchMedia = async () => {
            try {
                const response = await axios.get('http://localhost:5000/media');
                setMediaList(response.data);
            } catch (error) {
                console.error('Error fetching media list:', error);
            }
        };

        fetchMedia();
    }, []);

    return (
        <div className='container'>
            <h1 className='heading'>Media list</h1>
            <button style={{
                backgroundColor: "#0062ff",
                color: 'white',
                alignItems: 'center',
                textAlign: 'center',
                justifyContent: "center",
                alignSelf:'flex-end',
                borderWidth: 0,
                fontSize: 12,
                fontWeight: 600,
                cursor: 'pointer',
                marginTop: 0, 
                padding: 10, borderRadius: 10, width: "10%",marginBottom:5
            }}
            > Upload Image</button>
            <div className='innercontainer2' style={{margin:0,backgroundColor:'rgb(88, 128, 209)'}}>
                {mediaList.map(media => (
                    <div className='container3' key={media.id} >

                        <Link style={{margin:0,padding:0}} to={`/media/${media.id}`}>
                            <img style={{margin:0,padding:0}} src={media.thumbnailUrl} alt={media.title} width="100%" height='50%' />
                            <h2>{media.title}</h2>
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MediaList;
