// frontend/src/components/UploadForm.js
import React, { useState } from 'react';
import axios from 'axios';
import './index.css'
const UploadForm = () => {
    const [title, setTitle] = useState('ssssssss');
    const [description, setDescription] = useState('sssssss');
    const [thumbnail, setThumbnail] = useState(null);
    const [video, setVideo] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        formData.append('thumbnail', thumbnail);
        formData.append('video', video);
console.log(formData)
        try {
            await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            alert('Upload successful!');
        } catch (error) {
            console.error('Error uploading media:', error);
        }
    };

    return (
        <form className='container' style={{backgroundColor:'skyblue'}} onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                maxLength="50"
                required
                 className="commontextinpustyle"
            />
            <textarea
                placeholder="Description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                maxLength="200"
                required
            />
            <input
                type="file"
                accept="image/jpeg,image/png"
                onChange={(e) => setThumbnail(e.target.files[0])}
                required
            />
            <input
                type="file"
                accept="video/mpg,video/avi,video/mp4"
                onChange={(e) => setVideo(e.target.files[0])}
                required
            />
            <button type="submit">Upload</button>
        </form>
    );
};

export default UploadForm;
